import { Pipe, PipeTransform } from '@angular/core';
import { QuestionCategory } from './contact-question.model';

@Pipe({name: 'contactQuestionCategory'})
export class ContactQuestionCategoryPipe implements PipeTransform {
  transform(value: QuestionCategory): string {
    let result = '문의 분류';
    if (value === 'JOIN') {
      result = '회원가입 및 정보';
    }

    if (value === 'PAYMENT') {
      result = '결제 및 환불';
    }

    if (value === 'ETC') {
      result = '기타';
    }
    return result;
  }
}

import { Pipe, PipeTransform } from '@angular/core';
import { DomSanitizer } from '@angular/platform-browser';

@Pipe({
  name: 'securityTrustUrl'
})

export class SecurityTrustUrlPipe implements PipeTransform {

  constructor(private sanitizer: DomSanitizer) {
  }
  transform(value: any, ...args: any[]): any {
    return this.sanitizer.bypassSecurityTrustResourceUrl(value);
  }
}

