import { Injectable } from '@angular/core';
import { Router } from '@angular/router';
import { AuthService } from '../auth/auth.service';
import { IUser } from '../../model/auth.model';
import { SessionStorageService } from '../../module/storage/session-storage.service';

const MINUTES_UNITL_AUTO_LOGOUT = 60; // in mins
const CHECK_INTERVAL            = 15000; // in ms
// const CHECK_INTERVAL            = 1000; // in ms
const STORE_KEY                 = 'lastAction';

/*
* 1시간 로그아웃 기능
* */
@Injectable({
  providedIn: 'root'
})
export class AutoLogoutService {
  intervalId;

  loginUser: IUser;

  constructor(
    private router: Router,
    private authService: AuthService,
    private sessionStorage: SessionStorageService
  ) {}

  initAutoLogout(): void {
    this.check();
    this.initListener();
    this.initInterval();
    this.sessionStorage.set(STORE_KEY, Date.now().toString());
    this.authService.rxLoginUser().subscribe(loginUser => this.loginUser = loginUser)
  }

  initListener() {
    document.body.addEventListener('click', () => this.reset());
    document.body.addEventListener('mouseover', () => this.reset());
    document.body.addEventListener('mouseout', () => this.reset());
    document.body.addEventListener('keydown', () => this.reset());
    document.body.addEventListener('keyup', () => this.reset());
    document.body.addEventListener('keypress', () => this.reset());
  }

  reset() {
    if (this.loginUser) {
      return;
    }

    this.setLastAction(Date.now());
  }

  initInterval() {
    this.intervalId = setInterval(() => {
      this.check();
    }, CHECK_INTERVAL);
  }

  stopInterval(): void {
    if (this.intervalId) {
      clearInterval(this.intervalId);
    }
  }

  check() {
    if (!this.loginUser) {
      this.stopInterval();
      return;
    }

    const now       = Date.now();
    const timeleft  = this.getLastAction() + MINUTES_UNITL_AUTO_LOGOUT * 60 * 1000;
    const diff      = timeleft - now;
    const isTimeout = diff < 0;
    if (isTimeout) {
      alert('미사용 시간 1시간이 지나 자동으로 로그아웃 됩니다.');
      this.authService.logout();
    }
  }

  private getLastAction() {
    return parseInt(this.sessionStorage.get(STORE_KEY), 10);
  }

  private setLastAction(lastAction: number) {
    this.sessionStorage.set(STORE_KEY, lastAction.toString());
  }
}

import { Injectable } from '@angular/core';

@Injectable({
  providedIn: 'root'
})
export class CookieService {
  setCookie(cookieName: string, value: any, domain?: string, expires?: string) { // 로그인
    if (typeof value === 'undefined') {
      return;
    }

    if (typeof value === 'object') {
      value = JSON.stringify(value);
    }
    document.cookie = cookieName + '=' + encodeURI(value) +
      ((domain) ? '; domain=' + domain : '') + ((expires) ? '; expires=' + expires : '');
  }

  getCookieToString(cookieName: string) { // 처음 init
    let result = null;

    const cookies        = document.cookie.split(';');
    const findCookieList = cookies.filter(a => a.indexOf(cookieName) !== -1);

    if (findCookieList.length !== 0) {
      const cookie = findCookieList[0].split('=').pop();
      result     = decodeURI(cookie);
    }

    return result;
  }

  getCookieToObject(cookieName: string) { // 처음 init
    let result = null;

    const cookies        = document.cookie.split(';');
    const findCookieList = cookies.filter(a => a.indexOf(cookieName) !== -1);

    if (findCookieList.length !== 0) {
      const cookie = findCookieList[0].split('=').pop();

      try {
        result = JSON.parse(decodeURI(cookie));
      } catch (e) {
        console.error('CookieService: cookie parse error');
        result = null;
      }
    }

    return result;
  }

  deleteCookie(cookieName: string, domain?: string) { // 로그아웃하는
    document.cookie = cookieName + '=;' + 'expires=Thu, 01 Jan 1970 00:00:01 GMT;' + ((domain) ? 'domain=' + domain : '');
  }
}

// 로그인 할때 쿠키에다가도 저장
// 로그아웃 할때 쿠키에 있는 데이터를 지워야함.
// 처음 init할때 쿠키를 확인해서 loginUser 가 있으면 로그인을 시켜야해.

import { Injectable, ErrorHandler, Injector } from '@angular/core';
import { Router } from '@angular/router';
import { AuthService } from '../auth/auth.service';

@Injectable({
  providedIn: 'root'
})
export class ErrorHandlerService extends ErrorHandler {

  constructor(private injector: Injector) {
    super();
  }

  handleError(error: any): void {
    // 로컬인 경우에는 핸들러 작동하지 않음.
    const router = this.injector.get(Router);
    if (error.message.includes('Cannot match any routes') === true) {
      router.navigate([ '' ]);
    }

    if (window.location.hostname === 'localhost') {
      super.handleError(error);
      return;
    }

    let errorMessage = null;
    let errorStack   = null;
    let loginUserName = null;
    let loginUserNo   = null;
    const url           = window.location.href;
    const path          = window.location.pathname;
    const userAgent     = navigator.userAgent;

    if (typeof error === 'object') {
      errorMessage = error.message || null;
      errorStack   = error.stack || null;
    }
    if (typeof error === 'string') {
      errorMessage = error;
    }

    const authService = this.injector.get(AuthService);

    const loginUser = authService.getLoginUser();

    if (loginUser) {
      loginUserName = loginUser.name;
      loginUserNo   = loginUser.id;
    }

    const message = {
      url,
      path,
      userAgent,
      errorMessage,

      // null 허용
      errorStack,
      loginUserNo,
      loginUserName,
    };

    // const toolsApiService = this.injector.get(ToolsApiService);
    // const condition        = {
    //   transactor:  'webBiz',
    //   environment: ENV.NAME,
    //   message:     JSON.stringify(message)
    // };
    //
    // toolsApiService.createErrorLog(condition).subscribe((res) => {
    //   console.log('handleError', res);
    // });

    super.handleError(error);
  }
}

