import { Injectable } from '@angular/core';
import { Observable, Subject } from 'rxjs';
import { ToastParams } from './layout-taost.model';
import { BasicToastComponent } from './basic-toast/basic-toast.component';


@Injectable()
export class LayoutToastService {
  toast$: Subject<ToastParams> = new Subject();

  constructor() {
  }

  rxToast(): Observable<any> {
    return this.toast$;
  }

  create({ component, data = {}, during = 3000 }: { component: any, data?: any, during: number }): any {
    const id     = Symbol('toast');
    const isShow = true;

    const createToastParams = {
      id,
      isShow,
      component,
      data
    };

    this.toast$.next(createToastParams);

    setTimeout(() => {
      this.dismiss(id);
    }, during);

    return createToastParams;
  }

  dismiss(id, callback?: () => void): void {
    const isShow = false;

    this.toast$.next({
      id,
      isShow,
      callback
    });
  }

  alert(message): void {
    if (message === '') {
      return;
    }

    const id     = Symbol('toast');
    const isShow = true;
    const component = BasicToastComponent;
    const data = {
      message
    };

    const createToastParams = {
      id,
      isShow,
      component,
      data
    };

    this.toast$.next(createToastParams);

    setTimeout(() => {
      this.dismiss(id);
    }, 3000);
  }
}

import { Injectable } from '@angular/core';
import { environment } from '../../../../environments/environment';

@Injectable({
  providedIn: 'root'
})
export class LocalStorageService {
  private storage: Storage;

  private storageType: string = 'localStorage';

  private prefix: string = `${ environment.projectName }_${ environment.name }_`;

  private isSupport: boolean = false;

  constructor() {
    this._setLocalStorage();
  }

  get(key: string, prefix?: string): any {
    if (this.isSupport) {
      const deriveKey = this._deriveKey(key, prefix);
      let storageResult;
      if (this.storage.getItem(deriveKey)) {
        storageResult = JSON.parse(this.storage.getItem(deriveKey));
      }
      return storageResult;
    }
  }

  set(key: string, value: any, prefix?: string): void {
    if (this.isSupport) {
      const deriveKey = this._deriveKey(key, prefix);
      this.storage.setItem(deriveKey, JSON.stringify(value));
    }
  }

  remove(key: string, prefix?: string): void {
    if (this.isSupport) {
      const deriveKey = this._deriveKey(key, prefix);
      this.storage.removeItem(deriveKey);
    }
  }

  clear(key: string, prefix?: string): void {
    if (this.isSupport) {
      for (const localKey in this.storage) {
        const derivedKey = this._deriveKey(key, prefix);
        if (localKey === derivedKey) {
          this.storage.removeItem(derivedKey);
          break;
        }
      }
    }
  }

  clearAll(prefix?: string): void {
    if (this.isSupport) {
      for (const key in this.storage) {
        if (key.indexOf(prefix || this.prefix) === 0) {
          this.storage.removeItem(key);
        }
      }
    }
  }

  setPrefix(prefix: string): void {
    this.prefix = prefix;
  }

  private _deriveKey(key: string, prefix?: string): string {
    if (prefix === undefined || prefix === null) {
      return `${ this.prefix }${ key }`;
    } else {
      return `${ prefix }${ key }`;
    }
  }

  private _setLocalStorage(): void {

    if (window && window[this.storageType as any] !== undefined) {
      this.storage   = window[this.storageType as any] as any;
      this.isSupport = true;
    } else {
      this.isSupport = false;
    }
  }
}

import { Injectable } from '@angular/core';
import { environment } from '../../../../environments/environment';

@Injectable({
  providedIn: 'root'
})
export class SessionStorageService {
  private storage: Storage;

  private storageType: string = 'sessionStorage';

  private prefix: string = `${environment.projectName}_${environment.name}_`;

  private isSupport: boolean = false;

  constructor() {
    this._initSessionStorage();
  }

  get(key: string, prefix?: string): any {
    if (this.isSupport) {
      const deriveKey = this._deriveKey(key, prefix);
      let getSessionItem;
      if (this.storage.getItem(deriveKey) !== 'undefined' && this.storage.getItem(deriveKey) !== '') {
        getSessionItem = JSON.parse(this.storage.getItem(deriveKey));
      }
      return getSessionItem;
    }
  }

  set(key: string, value: any, prefix?: string): void {
    if (this.isSupport) {
      const deriveKey = this._deriveKey(key, prefix);
      this.storage.setItem(deriveKey, JSON.stringify(value));
    }
  }

  getAll(prefix?: string): any {
    const result = {};
    for (const key in this.storage) {
      if (key.indexOf(prefix || this.prefix) === 0) {
        const deriveKey = this._deriveKey(key, prefix);
        let getSessionItem;
        if (this.storage.getItem(deriveKey) !== 'undefined' && this.storage.getItem(deriveKey) !== '') {
          getSessionItem = JSON.parse(this.storage.getItem(deriveKey));
        }

        result[key] = getSessionItem;
      }
    }
    return result;
  }

  setSession(session: any, prefix?: string): void {
    for (const key of Object.keys(session)) {
      this.set(key, session[key], prefix);
    }
  }

  remove(key: string, prefix?: string): void {
    if (this.isSupport) {
      const deriveKey = this._deriveKey(key, prefix);
      this.storage.removeItem(deriveKey);
    }
  }

  clear(key: string, prefix?: string): void {
    if (this.isSupport) {
      for (const sessionKey in this.storage) {
        const derivedKey = this._deriveKey(key, prefix);
        if (sessionKey === derivedKey) {
          this.storage.removeItem(sessionKey);
          break;
        }
      }
    }
  }

  clearAll(prefix?: string): void {
    if (this.isSupport) {
      for (const key in this.storage) {
        if (key.indexOf(prefix || this.prefix) === 0) {
          this.storage.removeItem(key);
        }
      }
    }
  }

  setPrefix(prefix: string): void {
    this.prefix = prefix;
  }

  private _deriveKey(key: string, prefix?: string): string {
    if (prefix === undefined || prefix === null) {
      return `${this.prefix}${key}`;
    } else {
      return `${prefix}${key}`;
    }
  }

  private _initSessionStorage(): void {
    if (window && window[this.storageType as any] !== undefined) {
      this.storage = window[this.storageType as any] as any;
      this.isSupport = true;
    } else {
      this.isSupport = false;
    }
  }
}

import { Injectable } from '@angular/core';

@Injectable({
  providedIn: 'root'
})
export class StorageService {
  private storage: any = {};

  get(key: string): any {
    if (this.storage[key] instanceof Array) {
      return this.storage[key].slice();
    } else if (this.storage[key] instanceof Object) {
      return Object.assign({}, this.storage[key]);
    } else {
      return this.storage[key];
    }
  }

  set(key: string, value: any): void {
    if (value instanceof Array) {
      this.storage[key] = value.slice();
    } else if (value instanceof Object) {
      this.storage[key] = Object.assign({}, value);
    } else {
      this.storage[key] = value;
    }
  }

  remove(key: string): void {
    this.storage[key] = undefined;
  }

  getAll(): any {
    return Object.assign({}, this.storage);
  }

  setAll(data: any): any {
    this.storage = Object.assign({}, data);
  }

  clearAll(): void {
    this.storage = {};
  }
}

