import { Directive, HostListener, Input } from '@angular/core';
import { FocusService } from './focus.service';

/*
* click 이벤트에서 미리 입력받은 focusName 을 focusService 에 설정합니다.
* */
@Directive({
  selector: '[redcapxClickFocus]'
})
export class ClickFocusDirective {

  @Input('redcapxClickFocus') focusName;

  @HostListener('click', ['$event']) onClick(event) {
    if (!this.focusName) {
      return;
    }

    event.stopPropagation();

    const focusName = this.focusService.getFocus();
    if (this.focusName === focusName) {
      this.focusService.setFocus(null);

    } else {
      this.focusService.setFocus(this.focusName);
    }
  }

  constructor(private focusService: FocusService) { }


}

import { Directive, ElementRef, OnInit } from '@angular/core';
import { environment } from '../../../../environments/environment';

@Directive({
  selector: '[convertProdUrl]'
})
export class ConvertProdUrlDirective implements OnInit {
  constructor(private el: ElementRef) {
  }

  ngOnInit(): void {
    if (environment.production) {
      // http://capsule.redcapx.com/assets/images/chicken.jpeg => http://capsule.redcapx.com/static/assets/images/chicken.jpeg
      if (this.el.nativeElement.srcset && this.el.nativeElement.srcset !== '') {
        this.el.nativeElement.srcset = this.el.nativeElement.srcset.replace(/\/assets\//g, '/static/assets/');
      }

      const splitString = this.el.nativeElement.src.split('/');
      splitString.splice(3, 0, 'static');
      const recoveryUrl = splitString.join('/');
      this.el.nativeElement.src = recoveryUrl;
    }

  }
}

import { Directive, HostBinding, Input, OnInit } from '@angular/core';
import { environment } from '../../../../environments/environment';

@Directive({
  selector: '[EventDefaultImage]'
})
export class EventDefaultImageDirective implements OnInit {
  @Input() eventId: number;

  constructor() {}

  @HostBinding('style.background') private background;

  ngOnInit(): void {
    const productUrl = environment.production === true ? '/static' : '';
    const index = (this.eventId % 6) + 1;
    const imageUrl = `url('${productUrl}/assets/images/event-0${index}.png')`;
    this.background = imageUrl;
  }
}

import { Directive, ElementRef, OnInit } from '@angular/core';
import { environment } from '../../../../environments/environment';

@Directive({
  selector: '[imgSrcset]'
})
export class ImgSrcsetDirective implements OnInit {
  constructor(private el: ElementRef) {
  }

  ngOnInit(): void {
    if (this.el?.nativeElement?.src) {
      const srcSplitList = this.el.nativeElement.src.split('/');
      const srcSliceList = srcSplitList.slice(3);
      const src = '/' + srcSliceList.join('/');
      const srcSplitDotList = src.split('.');
      const fileType = srcSplitDotList.splice(-1, 1);
      const fileUrl = srcSplitDotList.join('.');
      this.el.nativeElement.srcset = `${src} 400w, ${fileUrl}@2x.${fileType} 700w, ${fileUrl}@3x.${fileType} 1000w`;
    }
  }
}

import { Directive, ElementRef, EventEmitter, Input, OnDestroy, OnInit, Output, Renderer2 } from '@angular/core';
import { FocusService } from './focus.service';
import { takeWhile } from 'rxjs/operators';

/*
* 포커스를 subscribe 하는 directive 로 적절한 위치에 directive 를 놓은뒤 예를들어 #focus="redcapFocus"로 변수에 할당받고
* focus.isFocus 를 확인해서 원하는 처리를 하면됩니다.
* */
@Directive({
  selector: '[redcapxOnFocus]',
  exportAs: 'redcapFocus'
})
export class OnFocusDirective implements OnInit, OnDestroy {

  isFocus: boolean;

  @Input('redcapxOnFocus') focusName;

  isUnsubscribe: boolean = false;

  constructor(
    private focusService: FocusService
  ) {
  }

  ngOnInit(): void {
    this.focusService.rxFocus()?.pipe(takeWhile(() => this.isUnsubscribe === false)).subscribe(focusName => {
      this.isFocus = this.focusName === focusName;
    });
  }

  ngOnDestroy(): void {
    this.isUnsubscribe = true;
  }
}

import { Directive, ElementRef, Input, OnChanges, OnInit, SimpleChanges, ViewChildren } from '@angular/core';
import { WaitHandler } from '../../handler/wait-handler/wait-handler';

@Directive({
  selector: '[scrollToValue]'
})
export class ScrollToValueDirective implements OnInit, OnChanges {
  @Input() scrollToValue: string;
  @Input() scrollSelectorClass: string;

  childList: any;
  selectedElement: any;

  constructor(
    private el: ElementRef
  ) {
  }

  ngOnInit(): void {
    const currentElement:Element = this.el.nativeElement;
    WaitHandler.wait(() => {
      const childElementList = currentElement.getElementsByClassName(this.scrollSelectorClass);
      return childElementList.length === 0 ? null : childElementList;
    }, () => {
      this.childList = currentElement.getElementsByClassName(this.scrollSelectorClass);
      this.setScroll();
    });
  }

  ngOnChanges(changes: SimpleChanges): void {
    if (changes?.['scrollToValue'] && this.childList) {
      this.setScroll();
    }
  }

  setScroll(): void {
    Array.prototype.forEach.call(this.childList, (elementItem: Element) => {
      if (elementItem.textContent === this.scrollToValue) {
        this.selectedElement = elementItem;
      }
    });

    const currentElement:Element = this.el.nativeElement;
    if (currentElement) {
      currentElement.scrollTo({
        left: 0,
        top: this.selectedElement.offsetTop
      });
    }
  }

}

