import { Component, OnDestroy, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { ApiUrl } from '../../core/constant/api.contant';
import { ApiService } from '../../core/service/api/api.service';
import { AuthService } from '../../core/service/auth/auth.service';
import { Router } from '@angular/router';
import { takeWhile } from 'rxjs/operators';
import { Subscription } from 'rxjs';
import { LayoutToastService } from '../../layout/toast/layout-toast.service';
import { Validator } from '../../core/validator/validator';
import { QuestionForm, ContactQuestionCondition } from './contact-question.model';
import { LayoutHeaderService } from '../../layout/header/layout-header.service';
import { FocusService } from '../../core/module/focus/focus.service';

@Component({
  selector: 'app-contact-question',
  templateUrl: './contact-question.component.html',
  styleUrls: ['./contact-question.component.scss']
})
export class ContactQuestionComponent implements OnInit, OnDestroy {
  contactForm: FormGroup;

  isShowValidLine: boolean = false;
  isUnsubscribe: boolean   = false;

  SContactRequest: Subscription = null;

  constructor(
    private authService: AuthService,
    private formBuilder: FormBuilder,
    private apiService: ApiService,
    private headerService: LayoutHeaderService,
    private focusService: FocusService,
    private router: Router
  ) {
  }

  ngOnInit(): void {
    const loginUser = this.authService.getLoginUser();
    this.contactForm = this.getContactForm(loginUser);
    this.headerService.themeBlack();
    this.headerService.show();

    console.log('contactForm.valid', this.contactForm.valid);
  }

  ngOnDestroy(): void {
    this.isUnsubscribe = true;
  }


  /******************************     event functions     ****************************/


  onClickCategory(category): void {
    this.focusService.setFocus(null);
    this.contactForm?.get('category').setValue(category);
  }

  onContact(): void {
    this.isShowValidLine = true;

    if (!this.contactForm?.get('category')?.valid) {
      alert('문의분류를 선택해 주세요.');
      return;
    }

    if (!this.contactForm?.get('email')?.valid) {
      alert('이메일을 확인해 주세요.');
      return;
    }

    if (!this.contactForm?.get('content')?.valid) {
      alert('내용을 입력해 주세요.');
      return;
    }

    const contactFormValue = this.contactForm.value;
    this.requestContact(contactFormValue);
  }


  /******************************     request functions     ****************************/


  private requestContact({ email, category, content }: QuestionForm) {
    if (this.SContactRequest) {
      alert('처리중 입니다.');
      return;
    }
    const url = ApiUrl.contact;

    // 바로 값을 전달할 수도 있지만 일부러 명시함.
    const body: ContactQuestionCondition = {
      type: 'QUESTIONS',
      email,
      category,
      content
    };
    this.SContactRequest = this.apiService.post({
      url,
      body
    })?.pipe(takeWhile(() => this.isUnsubscribe === false))
      .subscribe(res => {
        console.log('requestContact res => ', res);
        alert('입력하신 이메일 주소로 곧 답변드리겠습니다.');
        // alert('입력하신 이메일 주소로 곧 답변드리겠습니다.');
        this.router.navigateByUrl('main');
      }, error => {
        console.log('requestContact error =>', error);
      }, () => {
        this.SContactRequest = null;
      });
  }


  /******************************     etc functions     ****************************/


  private getContactForm(loginUser): FormGroup {
    return this.formBuilder.group({
      email:    [
        loginUser?.email || '',
        [
          Validators.required,
          Validator.email()
        ]
      ],
      category: [
        '',
        Validators.required
      ],
      content:  [
        '',
        Validators.required
      ]
    });
  }
}

