import { ChangeDetectorRef, Component, ComponentFactoryResolver, OnInit, ViewChild, ViewContainerRef } from '@angular/core';
import { Observable } from 'rxjs';
import { ComponentHandler } from '../../core/handler/component-handler/component-handler';
import { TOAST_ANIMATION_TIME, ToastParams } from './layout-taost.model';
import { LayoutToastService } from './layout-toast.service';

@Component({
  selector: 'redcapx-layout-toast',
  templateUrl: './layout-toast.component.html',
  styleUrls: ['./layout-toast.component.scss']
})
export class LayoutToastComponent implements OnInit {

  @ViewChild('target', { read: ViewContainerRef })
  target: any;

  toast$: Observable<any>;

  componentReferenceList: any[] = [];

  constructor(
    private resolver: ComponentFactoryResolver,
    private changeDetectorRef: ChangeDetectorRef,
    private toastService: LayoutToastService) {
  }

  ngOnInit() {
    this.toast$ = this.toastService.rxToast();

    this.toast$.subscribe((params: ToastParams) => {
        const component = params.component;
        const data    = params.data || {};

        if (params.isShow) {
          this.changeDetectorRef.detectChanges();
          data.index = this.componentReferenceList.length;
          data.id = params.id;
          const componentReference = ComponentHandler.createComponent(this.resolver, this.target, component, data);
          this.componentReferenceList.push(componentReference);
        }

        if (!params.isShow) {
          this.dismissToast(params.id);

          if (this.componentReferenceList.length === 0) {}
          this.changeDetectorRef.detectChanges();
        }
      }
    );
  }

  dismissToast(id) {
    const dismissIndex = this.componentReferenceList.findIndex((componentReference) => {
      return componentReference.instance['id'] === id
    });
    console.log('dismissIndex', dismissIndex);
    if(dismissIndex === -1) {
      return;
    }

    const dismissItem: any = this.componentReferenceList.splice(dismissIndex, 1)[0];
    if (dismissItem) {
      dismissItem?.destroy();
    }

    setTimeout(() => {
      for(let index = 0; index < this.componentReferenceList.length; index++) {
        const item = this.componentReferenceList?.[index];
        if (!item) {
          break;
        }

        item.instance['index'] = index;
      }
    }, TOAST_ANIMATION_TIME)
  }
}

import { Component, OnDestroy, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { ApiUrl } from '../../core/constant/api.contant';
import { takeWhile } from 'rxjs/operators';
import { ApiService } from '../../core/service/api/api.service';
import { Validator } from '../../core/validator/validator';
import { AuthService } from '../../core/service/auth/auth.service';
import { LayoutModalService } from '../../layout/modal/layout-modal.service';
import { FindPwModalComponent } from '../../shared/modals/find-pw/find-pw-modal.component';
import { Router } from '@angular/router';
import { ILoginApiCondition } from './login.model';
import { IUser } from '../../core/model/auth.model';
import { Subscription } from 'rxjs';
import { AutoLogoutService } from '../../core/service/auto-logout/auto-logout.service';
import { LayoutHeaderService } from '../../layout/header/layout-header.service';

@Component({
  selector:    'redcapx-login',
  templateUrl: './login.component.html',
  styleUrls:   [ './login.component.scss' ]
})
export class LoginComponent implements OnInit, OnDestroy {

  loginForm: FormGroup;

  isAutoLogin: boolean     = false; // 로컬스토리지에 key 값 저장
  isShowValidLine: boolean = false; // 폼 벨리데이션 에러 표시
  isUnsubscribe: boolean   = false;

  SLoginRequest: Subscription = null;

  constructor(
    private formBuilder: FormBuilder,
    private apiService: ApiService,
    private authService: AuthService,
    private modalService: LayoutModalService,
    private autoLogoutService: AutoLogoutService,
    private router: Router,
    private _layoutHeaderService: LayoutHeaderService
  ) {
  }

  ngOnInit(): void {
    this._layoutHeaderService.hide();
    this.loginForm = this.getLoginForm();
  }

  ngOnDestroy(): void {
    this.isUnsubscribe = true;
  }

  /******************************     Event functions     ****************************/

  onLogin(): void {
    this.isShowValidLine = true;

    if(!this.loginForm.get('email')?.valid) {
      alert('이메일을 확인해 주세요.');
      return;
    }

    if(!this.loginForm.get('password')?.valid) {
      alert('비밀번호를 확인해 주세요.');
      return;
    }


    const { email, password } = this.loginForm.value;
    this.requestLogin({
      email,
      password
    });
  }

  onShowFindPw(): void {
    this.modalService.create(FindPwModalComponent, {
      dismiss: () => this.modalService.dismiss()
    });
  }


  /******************************     etc functions     ****************************/

  private getLoginForm(): FormGroup {
    return this.formBuilder.group({
      email:       [
        '',
        [
          Validators.required,
          Validator.email()
        ]
      ],
      password:    [
        '',
        [
          Validators.required,
          Validators.minLength(8),
        ]
      ],
      isAutoLogin: false
    });
  }

  /******************************     request functions     ****************************/

  private requestLogin({ email, password }: ILoginApiCondition) {
    if (this.SLoginRequest) {
      alert('로그인 중입니다.');
      return;
    }

    const url  = ApiUrl.login;
    const body = {
      email,
      password
    };

    this.SLoginRequest = this.apiService.post({
      url,
      body
    })?.pipe(takeWhile(() => this.isUnsubscribe === false))
      .subscribe((loginUser: IUser) => {
        this.SLoginRequest = null;

        console.log('requestLogin res => ', loginUser);
        this.authService.login(loginUser);
        // this.autoLogoutService.initAutoLogout();

        // 자동 로그인 키값 저장
        if (this.loginForm.get('isAutoLogin').value === true) {
          this.authService.setAutoLogin(loginUser.key);
        }

        // 되돌아갈 페이지가 있다면 해당 페이지로 이동
        const redirectUrl = this.authService.getRedirectUrl();

        if (redirectUrl) {
          this.authService.setRedirectUrl(null);
          this.router.navigateByUrl(redirectUrl);
        } else {
          this.router.navigateByUrl('');
        }
      }, error => {
        this.SLoginRequest = null;
        console.log('requestLogin error =>', error);
        const message = error?.error?.details?.[0].message;
        if (message instanceof Array && message[0] === '제공된 인증데이터(credentials)로는 로그인할 수 없습니다.') {
          alert('입력하신 정보로 로그인할 수 없습니다.');
        } else if(!message) {
          alert('로그인을 할 수 없습니다.');
        } else {
          alert(message);
        }
      });
  }
}

