import { ChangeDetectorRef, Component, OnDestroy, OnInit, ViewEncapsulation } from '@angular/core';
import { Meta } from '@angular/platform-browser';
import { ApiService } from '../../core/service/api/api.service';
import { Platform } from '@angular/cdk/platform';
import { ActivatedRoute, Router } from '@angular/router';
import { SwiperConfig } from 'ngx-swiper-wrapper';
import { IVocaTest } from '../../core/model/voca.model';
import { IBookmark } from '../../core/model/bookmark.model';
import { LayoutHeaderService } from '../../layout/header/layout-header.service';
import { IMagazine } from '../../core/model/magazine.model';
import { Observable } from 'rxjs';
import { LayoutService } from '../../core/service/layout/layout.service';

@Component({
  selector:      'redcapx-main',
  templateUrl:   './main.component.html',
  styleUrls:     [ './main.component.scss' ],
  encapsulation: ViewEncapsulation.None /* swiper 에서 동적으로 생성시키는 pagination 의 css를 적용시키기 위해서 설정 */
})
export class MainComponent implements OnInit, OnDestroy {
  vocaTagSwiperConfig: any;
  vocaTestSwiperConfig: any;

  swiperIndex: number = 0;

  vocaTestList: IVocaTest[];
  magazineList: IMagazine[];
  bookmarkList: IBookmark[];
  isMobile$: Observable<boolean>;

  private isUnsubscribe: boolean;

  constructor(
    private _route: ActivatedRoute,
    private router: Router,
    private meta: Meta,
    private apiService: ApiService,
    private platform: Platform,
    private changeDetectorRef: ChangeDetectorRef,
    private layoutService: LayoutService,
    private _layoutHeaderService: LayoutHeaderService
  ) {
  }

  ngOnInit(): void {
    this._layoutHeaderService.show();
    this._layoutHeaderService.themeWhite();

    this.vocaTestList = this._route.snapshot.data?.vocaTestList;
    this.bookmarkList = this._route.snapshot.data?.bookmarkList.slice(0, 2);
    this.magazineList = this._route.snapshot.data?.magazineList;
    console.log('this.magazineList', this.magazineList);
    console.log('vocaTestList', this.vocaTestList);

    this.changeDetectorRef.detectChanges();
    this.initSwiper();

    this.isMobile$ = this.layoutService.rxIsMobile();
    this.layoutService.rxIsMobile().subscribe(isMobile => {
      if (isMobile) {
        this.changeDetectorRef.detectChanges();
        this.initSwiper();
      }
    })
  }

  ngOnDestroy(): void {
    this.isUnsubscribe = true;
  }

  /******************************     event functions     ****************************/

  onStudyWord(vocaTestId, wordIndex): void {
    this.router.navigate([
      'study',
      vocaTestId
    ], {
      queryParams: {
        word_index: wordIndex
      }
    });
  }


  /******************************     init functions     ****************************/
  private initSwiper(): void {
    if (this.platform.isBrowser) { // 이게 안드로이드나 ios 앱 에서도 true 이까?
      this.vocaTestSwiperConfig = new SwiperConfig({
        slidesPerView:  'auto',
        centeredSlides: true,
        slideClass:     'voca-test-item'
      });

      this.vocaTagSwiperConfig = new SwiperConfig({
        slidesPerView:       'auto',
        centeredSlides:      true,
        slideClass:          'voca-tag-item',
        slideToClickedSlide: true
      });
    }
  }


  onGotoTest(vocaTestItem: IVocaTest, type: 'basic' | 'advance') {
    this.router.navigate(['voca-test'], {
      queryParams: {
        id: vocaTestItem.id,
        type
      }
    })
  }
}

