  &.business {
    background: linear-gradient(#051033, #000000);
  }
  &.tech {
    background: linear-gradient(#260F04, #000000);
  }

  .voca-test-swiper-container {
    width: 100%;
    margin-top: 20px;
  }

  .voca-test-item {
    height: 524px;
    flex-shrink: 0;
    margin: 0 10px;

    @include pc-style {
      display: flex;
      justify-content: center;
      height: auto;
      margin: 16px 12px;
      &:first-child {
        margin-top: 40px;
      }
    }

    .box {
      width: 204px;
      position: relative;
      height: 100%;
      margin-top: 8px;
      @include pc-style {
        height: 180px !important;
        margin-top: 0;
        margin-right: 8px;
      }
      border-radius: 8px;
      &:first-child {
        margin-top: 0;
        background: white;
      }
      .label {
        position: absolute;
        top: 10px;
        left: 10px;
        padding: 6px 11px 5px;
        font-size: 12px;
        line-height: 12px;
        font-weight: bold;
        border-radius: 4px;
      }
      .button-wrapper {
        position: absolute;
        bottom: 17px;
        left: 24px;
        .button {
          display: flex;
          justify-content: center;
          align-items: center;
          width: 156px;
          height: 48px;
          background: white;
          font-size: 16px;
          border-radius: 50px;
          cursor: pointer;
        }
      }

      &.category {
        background: none;
        color: white;

        font-family: Montserrat;
        font-style: normal;
        font-weight: bold;
        font-size: 28px;
        line-height: 40px;
      }

      &.level-wrapper {
        position: relative;
        font-family: 'Montserrat', sans-serif;
        .background-half {
          position: absolute;
          width: 100%;
          height: 100%;
          -webkit-clip-path: polygon(100% 0,0 100%,100% 100%);
          clip-path: polygon(100% 0,0 100%,100% 100%);
          border-radius: 8px;
        }
        &.lv-1 {
          background: $lv-0;
          .background-half {
            background: $lv-1;
          }
        }
        &.lv-10 {
          background: $lv-1;
          .background-half {
            background: $lv-2;
          }
        }
        &.lv-20 {
          background: $lv-2;
          .background-half {
            background: $lv-3;
          }
        }
        &.lv-30 {
          background: $lv-3;
          .background-half {
            background: $lv-4;
          }
        }
        &.lv-40 {
          background: $lv-4;
          .background-half {
            background: $lv-5;
          }
        }
        &.lv-50 {
          background: $lv-5;
          .background-half {
            background: $lv-6;
          }
        }
        &.lv-60 {
          background: $lv-6;
          .background-half {
            background: $lv-7;
          }
        }
        &.lv-70 {
          background: $lv-7;
          .background-half {
            background: $lv-8;
          }
        }
        &.lv-80 {
          background: $lv-8;
          .background-half {
            background: $lv-9;
          }
        }
        &.lv-90 {
          background: $lv-9;
          .background-half {
            background: $lv-10;
          }
        }
        &.lv-100 {
          background: linear-gradient(122deg, #202020, #303030);
          .background-half {
            background: linear-gradient(122deg, $lv-11, #202020);
          }
        }
        .label {
          color: white;
          background: rgba(255,255,255, 0.2);
        }
        height: 164px;
        background: slateblue;
        .level {
          position: absolute;
          width: 100%;
          color: white;
          margin-top: 28px;
          @include pc-style {
            margin-top: 40px;
          }

          font-size: 48px;
          font-weight: bold;
          text-align: center;
          .lv {
            font-size: 12px;
            margin-right: 4px;
          }
        }

      }
      &.word-wrapper {
        height: 180px;
        background: white;

