    }
  }

  .voca-tag-swiper-container {
    width: 100%;
    //height: 100%;
    padding-top: 72px;
  }

  .voca-tag-item {
    font-family: 'Montserrat', sans-serif;
    padding: 10px;
    font-weight: bold;
    color: white;
    font-size: 20px;
    flex-shrink: 0;
    margin: 0 10px;
    cursor: pointer;
  }
}

.main-page {
