import { Component, ElementRef, OnDestroy, OnInit, ViewChild } from '@angular/core';
import { FormBuilder, FormControl, FormGroup, Validators } from '@angular/forms';
import { LayoutService } from '../../core/service/layout/layout.service';
import { map, takeWhile } from 'rxjs/operators';
import { FormHandler } from '../../core/handler/form-handler/form-handler';
import { ActivatedRoute } from '@angular/router';
import { AuthService } from '../../core/service/auth/auth.service';
import { environment } from '../../../environments/environment';
import { ApiService } from '../../core/service/api/api.service';
import { ApiUrl } from '../../core/constant/api.contant';
import { IUser } from '../../core/model/auth.model';
import { LayoutHeaderService } from '../../layout/header/layout-header.service';
import { combineLatest, Observable, Subscription, timer } from 'rxjs';
import { IMembershipPayment } from '../../core/model/payment.model';
import { Validator } from '../../core/validator/validator';

declare const goPay: (formData: any) => void;

@Component({
  selector:    'app-purchase',
  templateUrl: './purchase.component.html',
  styleUrls:   [ './purchase.component.scss' ]
})
export class PurchaseComponent implements OnInit, OnDestroy {
  @ViewChild('payFormElement') payFormElement: ElementRef;
  membershipPayment: IMembershipPayment;
  purchaseForm: FormGroup;
  loginUser: IUser;
  directShowOpt: string   = '';
  nicepayReserved: string = '';

  paymentAmount: number;
  paymentUrl: string;
  isMobile: boolean;
  isShowValidLine: boolean;
  isUnsubscribe: boolean           = false;
  isSendVerify: boolean            = false;
  isPhoneNumber: boolean           = false;
  verifyCodeControl: FormControl   = new FormControl('', [ Validators.required ]);
  saleCodeControl: FormControl     = new FormControl('', [ Validators.required ]);
  paymentTypeControl: FormControl  = new FormControl('', [ Validators.required ]);
  allAgreeTermControl: FormControl = new FormControl(false, [ Validators.requiredTrue ]);
  agreeTermTwoControl: FormControl = new FormControl(false, [ Validators.requiredTrue ]);
  agreeTermOneControl: FormControl = new FormControl(false, [ Validators.requiredTrue ]);
  phoneNumberControl: FormControl  = new FormControl('', [
      Validators.required,
      Validator.mobile()
    ]
  );

  timer$: Observable<number>;

  SPhoneNumberVerifyRequest: Subscription;
  SResendVerifyCodeRequest: Subscription;
  SGetSaleRequest: Subscription;
  SUpdatePaymentRequest: Subscription;

  SVerifyCodeRequest: Subscription;
  PAYMENT_URL = environment.apiUrl + '/' + ApiUrl.payment;
  paymentType = {
    '신용카드':  {
      nicepayReserved: '',
      directShowOpt:   ''
    },
    '페이코':   {
      nicepayReserved: 'DirectPayco=Y',
      directShowOpt:   'CARD'
    },
    '카카오페이': {
      nicepayReserved: 'DirectKakao=Y',
      directShowOpt:   'CARD'
    },
    '삼성페이':  {
      nicepayReserved: 'DirectSamsungPay=Y',
      directShowOpt:   'CARD'
    }
  };

  constructor(
    private _formBuilder: FormBuilder,
    private _layoutService: LayoutService,
    private _route: ActivatedRoute,
    private _auth: AuthService,
    private _headerService: LayoutHeaderService,
    private _apiService: ApiService
  ) {
  }

  ngOnInit(): void {
    this._headerService.show();
    this._headerService.themeWhite();

    this.loginUser         = this._auth.getLoginUser();
    this.isPhoneNumber     = !!this.loginUser.phone_number;
    this.membershipPayment = this._route.snapshot.data?.membershipPayment;
    this.paymentAmount     = this.membershipPayment.amount;
    this._layoutService.rxIsMobile().pipe(takeWhile(() => this.isUnsubscribe === false)).subscribe(isMobile => {
      this.isMobile   = isMobile;
      this.paymentUrl = !isMobile ? this.PAYMENT_URL : 'https://web.nicepay.co.kr/v3/v3Payment.jsp';
    });
    this.purchaseForm       = this._getPurchaseForm(this.membershipPayment);
    window['nicepaySubmit'] = this._nicepaySubmit;
    window['nicepayClose']  = this._nicepayClose;

    this._subscribeChangeTermValue();
  }

  ngOnDestroy(): void {
    this.isUnsubscribe = false;
  }

  onPurchase() {
    this.isShowValidLine = true;
    if (!this.paymentTypeControl.valid) {
      return;
    }

    if (!this.allAgreeTermControl.valid) {
      return;
    }

    const paymentTypeInfo = this.paymentType[this.paymentTypeControl.value];
    this.purchaseForm.get('NicepayReserved').setValue(paymentTypeInfo.nicepayReserved);
    this.purchaseForm.get('DirectShowOpt').setValue(paymentTypeInfo.directShowOpt);

    const formValue = this.purchaseForm.value;
    console.log('this.purchaseForm.value', this.nicepayReserved, this.directShowOpt, this.purchaseForm.value);
    const formData  = FormHandler.convertObjectToFormData(formValue);
    if (!this.isMobile) {
      console.log('?', this.payFormElement.nativeElement);
      goPay(this.payFormElement.nativeElement);
    } else {
      this.payFormElement.nativeElement.submit();
    }
  }

  onSendVerifyCode(): void {
    this.isShowValidLine = true;
    if (!this.phoneNumberControl.valid) {
      return;
    }

    const phoneNumber = this.phoneNumberControl.value;
    this._requestVerifyCode({ phoneNumber });
  }

  onVerifyCode() {
    if (!this.verifyCodeControl.valid) {
      return;
    }
    const phoneNumber       = this.phoneNumberControl.value;
    const codeByPhoneNumber = this.verifyCodeControl.value;
    this._requestPhoneNumberVerify({
      phoneNumber,
      codeByPhoneNumber
    });
  }

  onResendVerifyCode(): void {
    const phoneNumber = this.phoneNumberControl.value;
    this._requestResendVerifyCode({ phoneNumber });
  }

  onClickAllTerm($event, value): void {
    $event.preventDefault();
    this.agreeTermOneControl.setValue(!value);
    this.agreeTermTwoControl.setValue(!value);
  }

  onGetSale(): void {
    if (!this.saleCodeControl.valid) {
      return;
    }

    const code = this.saleCodeControl.value;
    const amount = this.paymentAmount;

    this._requestGetSale({ code, amount });
  }


  /******************************     request functions     ****************************/

  private _requestVerifyCode({ phoneNumber }: { phoneNumber: string }) {
    if (this.SVerifyCodeRequest) { // 연속 처리 방지
      alert('잠시만 기다려주세요.');
      return;
    }

    const url                          = ApiUrl.phoneNumber;
    const body                         = { phone_number: phoneNumber };
    this.SVerifyCodeRequest = this._apiService.post({
      url,
      body
    })?.pipe(takeWhile(() => this.isUnsubscribe === false))
      .subscribe(res => {
        console.log('requestVerifyCode res => ', res);
        this.SVerifyCodeRequest = null;

        this.isSendVerify    = true;
        this.isShowValidLine = false;
        this.setTimer();
        alert('인증 코드가 발송되었습니다.');
      }, error => {
        console.log('requestVerifyCode error =>', error);
        this.SVerifyCodeRequest = null;
        alert('코드 발송중 오류가 발생했습니다.');
      });
  }

  private _requestResendVerifyCode({ phoneNumber }: { phoneNumber: string }): void {
    if (this.SResendVerifyCodeRequest) { // 연속 처리 방지
      alert('잠시만 기다려주세요.');
      return;
    }

    const url                                = ApiUrl.phoneNumberResend;
    const body                               = {
      phone_number: phoneNumber
    };
    this.SResendVerifyCodeRequest = this._apiService.post({
      url,
      body
    })?.pipe(takeWhile(() => this.isUnsubscribe === false))
      .subscribe(res => {
        console.log('requestResendVerifyCode res => ', res);
        this.SResendVerifyCodeRequest = null;
        alert('코드를 재전송 하였습니다.');
        this.setTimer();
      }, error => {
        console.log('requestResendVerifyCode error =>', error);
        if (error?.error?.code === 100004) {
          alert('3분동안 연속으로 3번 이상 재전송 요청 할 수 없습니다.');
        }
        this.SResendVerifyCodeRequest = null;
      });
  }

  private _requestPhoneNumberVerify({ phoneNumber, codeByPhoneNumber }: { phoneNumber: string, codeByPhoneNumber: string }) {
    if (this.SPhoneNumberVerifyRequest) { // 연속 처리 방지
      alert('잠시만 기다려주세요.');
      return;
    }

    const url                                 = ApiUrl.phoneNumberVerify;
    const body                                = {
      phone_number:         phoneNumber,
      code_by_phone_number: codeByPhoneNumber
    };
    this.SPhoneNumberVerifyRequest = this._apiService.post({
      url,
      body
    })?.pipe(takeWhile(() => this.isUnsubscribe === false))
      .subscribe(res => {
        console.log('requestPhoneNumberVerify res => ', res);
        this.loginUser.phone_number = phoneNumber;
        this._auth.login(this.loginUser);
      }, error => {
        console.log('requestPhoneNumberVerify error =>', error);
        this.SPhoneNumberVerifyRequest = null;
        alert('코드인증 중 오류가 발생했습니다.');
      });
  }

  private _requestGetSale({ code, amount }) {
    if (this.SGetSaleRequest) { // 연속 처리 방지
      alert('잠시만 기다려주세요.');
      return;
    }

    const url                       = ApiUrl.getSale;
    const body                      = {
      code,
      amount
    };
    this.SGetSaleRequest = this._apiService.post({
      url,
      body
    }).pipe(takeWhile(() => this.isUnsubscribe === false))
      .subscribe(res => {
        console.log('requestGetSale res => ', res);
        // 이거 받은 후 업데이트 쳐야함.

        this._requestUpdatePayment({amount: this.paymentAmount - res.sales_amount});
        this.SGetSaleRequest = null;
      }, error => {
        console.log('requestGetSale error =>', error);
        this.SGetSaleRequest = null;
      });
  }

  private _requestUpdatePayment({ amount }) {
    if (this.SUpdatePaymentRequest) { // 연속 처리 방지
      alert('잠시만 기다려주세요.');
      return;
    }

    const url = ApiUrl.paymentUpdate;
    const body = { amount };
    this.SUpdatePaymentRequest = this._apiService.post({
      url, body
    }).pipe(takeWhile(() => this.isUnsubscribe === false))
      .subscribe(res => {
        console.log('requestUpdatePayment res => ', res);
        this.paymentAmount = res.amount;
        this.purchaseForm.get('Amt').setValue(this.paymentAmount);
        this.purchaseForm.get('EdiDate').setValue(res?.nice_data?.edi_date);
        this.purchaseForm.get('SignData').setValue(res?.nice_data?.sign_data);

        this.SUpdatePaymentRequest = null;
      }, error => {
        console.log('requestUpdatePayment error =>', error);
        this.SUpdatePaymentRequest = null;
      });
  }

  /******************************     etc functions     ****************************/



  private _getPurchaseForm(membershipPayment: IMembershipPayment): FormGroup {
    console.log('membershipPayment', membershipPayment);
    return this._formBuilder.group({
      'PayMethod':       'CARD',
      'GoodsName':       membershipPayment.membership.name,
      'Amt':             this.paymentAmount,
      'MID':             environment.mid,
      'Moid':            membershipPayment.id,
      'BuyerName':       this.loginUser.name,
      'BuyerEmail':      this.loginUser.email,
      'BuyerTel':        '',
      'ReturnURL':       this.PAYMENT_URL,
      'EdiDate':         membershipPayment?.nice_data?.edi_date,
      'SignData':        membershipPayment?.nice_data?.sign_data,
      'ReqReserved':     this.loginUser.key,
      'NicepayReserved': this.nicepayReserved,
      'DirectShowOpt':   this.directShowOpt,
      'GoodsCl':         0,
      'TransType':       0,
      'CharSet':         'utf-8'
    });
  }

  private _nicepaySubmit(): void {
    // payment update done
    console.log('submit');

    const payForm = document.getElementById('payForm') as any;
    payForm.submit();
    // const url = ApiUrl.payment;
    // this._apiService.post({
    //   url,
    //   body: this.payFormElement.nativeElement
    // }).subscribe(res => {
    //   console.log('res', res);
    // })
    // 안댐.
  }

  private _nicepayClose(): void {
    // payment update cancel
    console.log('close');
  }

  private setTimer(): void {
    const limitTime = 3 * 60 * 1000 + 1;  // 3분,  0.001 초 추가한것은 moment pipe 에 00:00 표시하기 위해서
    this.timer$     = timer(0, 1000)?.pipe(
      takeWhile((count) => {
        return (count * 1000) < limitTime;
      }),
      map(count => limitTime - (count * 1000))
    );
  }

  private _subscribeChangeTermValue() {
    combineLatest([
      this.agreeTermOneControl.valueChanges,
      this.agreeTermTwoControl.valueChanges
    ]).pipe(map((value) => {
      if ((value[0] === value[1]) && (value[0] === value[1]) !== this.allAgreeTermControl.value) {
        return value[0];
      }
    })).subscribe(value => {
      this.allAgreeTermControl.setValue(!!value);
    });
  }
}


