import { Component, OnDestroy, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Validator } from '../../core/validator/validator';
import { ApiService } from '../../core/service/api/api.service';
import { ActivatedRoute, Router } from '@angular/router';
import { SignupCondition, SignupForm } from './signup.model';
import { Subscription } from 'rxjs';
import { ApiUrl } from '../../core/constant/api.contant';
import { takeWhile } from 'rxjs/operators';
import { StorageService } from '../../core/module/storage/storage.service';
import { LayoutHeaderService } from '../../layout/header/layout-header.service';
import { LayoutModalService } from '../../layout/modal/layout-modal.service';
import { CheckEmailModalComponent } from '../../shared/modals/check-email/check-email-modal.component';

@Component({
  selector:    'redcapx-signup',
  templateUrl: './signup.component.html',
  styleUrls:   [ './signup.component.scss' ]
})
export class SignupComponent implements OnInit, OnDestroy {

  signupForm: FormGroup;

  isShowValidLine = false;
  isUnsubscribe: boolean = false;
  SFindEmailRequest: Subscription;
  SSignupRequest: Subscription;

  constructor(
    private formBuilder: FormBuilder,
    private apiService: ApiService,
    private router: Router,
    private route: ActivatedRoute,
    private storage: StorageService,
    private _headerService: LayoutHeaderService,
    private _modalService: LayoutModalService,
  ) {
  }

  ngOnInit(): void {
    this._headerService.hide();
    const signupPage = this._getSignupPage();
    this.signupForm   = this.getSignupForm(signupPage);
  }

  ngOnDestroy(): void {
    this.isUnsubscribe = true;
  }

  /******************************     event functions     ****************************/

  onNext(): void {
    this.isShowValidLine = true;

    if(!this.validateForm()) {
      return;
    }

    const { username, email, password1, password2 } = this.signupForm.value;
    this._requestSignup({ username, email, password1, password2 });
  }


  onGotoLogin() {
    this.router.navigateByUrl('login');
  }

  /******************************     request functions     ****************************/

  // private requestFindEmail({ username, email, password1, password2 }: SignupCondition) {
  //   if (this.SFindEmailRequest) { // 연속 처리 방지
  //     alert('이메일을 확인중입니다.');
  //     return;
  //   }
  //
  //   const url  = ApiUrl.findEmail;
  //   const body = { email };
  //
  //   this.SFindEmailRequest = this.apiService.post({
  //     url,
  //     body
  //   })?.pipe(takeWhile(() => this.isUnsubscribe === false))
  //     .subscribe(res => {
  //       console.log('requestFindEmail res => ', res);
  //       this.SFindEmailRequest = null;
  //       if (res.isFind === true) {
  //         alert('사용중인 이메일 입니다.');
  //         return;
  //       }
  //
  //     }, error => {
  //       console.log('requestFindEmail error =>', error);
  //       this.SFindEmailRequest = null;
  //     });
  // }

  private _requestSignup({ username, email, password1, password2 }: SignupCondition) {
    if (this.SSignupRequest) {
      alert('회원가입 처리중 입니다.');
      return;
    }
    const url                      = ApiUrl.signup;
    const body                     = {
      username,
      email,
      password1,
      password2,
    };

    this.SSignupRequest = this.apiService.post({
      url,
      body
    })?.pipe(takeWhile(() => this.isUnsubscribe === false))
      .subscribe(res => {
        this.SSignupRequest = null;
        console.log('requestSignup res => ', res);
        this.storage.remove('signup-page');

        this._showSignupDoneModal(email);
      }, error => {
        this.SSignupRequest = null;
        console.log('requestSignup error =>', error);
      });
  }

  /******************************     etc functions     ****************************/


  private getSignupForm(signupPage: SignupForm): FormGroup {
    const result = this.formBuilder.group({
      username:      [
        signupPage?.username || '',
        Validators.required
      ],
      email:         [
        signupPage?.email || '',
        [
          Validators.required,
          Validator.email()
        ]
      ],
      password1:     [
        signupPage?.password1 || '',
        [
          Validators.required,
          Validators.minLength(6),
          Validator.password(),
        ]
      ],
      password2:     [
        signupPage?.password2 || ''
      ],
      isTerm:   [
        signupPage?.isTerm || false,
        Validators.requiredTrue
      ]
    });

    // password 가 같은지 validation 추가
    result.get('password2').setValidators([
      Validators.required,
      Validator.passwordConfirm(result.get('password1'))
    ]);

    result.valueChanges.pipe(takeWhile(() => this.isUnsubscribe === false)).subscribe(value => {
      console.log('???');
      this._saveSignupPage(value);
    });
    return result;
  }

  private validateForm(): boolean {
    if (!this.signupForm.get('username')?.valid) {
      alert('이름을 확인해 주세요.');
      return;
    }

    if (!this.signupForm.get('email')?.valid) {
      alert('이메일을 확인해 주세요.');
      return;
    }

    if (!this.signupForm.get('password1')?.valid) {
      alert('비밀번호는 영어 대/소문자, 숫자, 특수문자를 혼합하여 8자리 이상으로 입력주세요.');
      return;
    }

    if (!this.signupForm.get('password2')?.valid) {
      alert('비밀번호가 같지 않습니다.');
      return;
    }

    if (!this.signupForm.get('isTerm')?.valid) {
      alert('이용약관, 개인정보 처리방침에 동의해 주세요.');
      return;
    }
    return true;
  }

  private _getSignupPage(): SignupForm {
    return this.storage.get('signup-page');
  }

  private _saveSignupPage(signupForm: SignupForm): void {
    this.storage.set('signup-page', signupForm);
  }

  private _showSignupDoneModal(email: string): void {
    this._modalService.create(CheckEmailModalComponent, {
      email,
      dismiss: () => {
        this.router.navigateByUrl('login');
        this._modalService.dismiss();
      }
    })
  }
}

