import { Component, OnDestroy, OnInit } from '@angular/core';
import { LayoutHeaderService } from '../../layout/header/layout-header.service';
import {
  ChartComponent,
  ApexAxisChartSeries,
  ApexChart,
  ApexXAxis,
  ApexYAxis,
  ApexDataLabels,
  ApexTitleSubtitle,
  ApexStroke,
  ApexGrid,
  ApexAnnotations
} from 'ng-apexcharts';
import { AuthService } from '../../core/service/auth/auth.service';
import { LayoutModalService } from '../../layout/modal/layout-modal.service';
import { EditPasswordModalComponent } from '../../shared/modals/edit-password/edit-password-modal.component';
import { EditUserModalComponent } from '../../shared/modals/edit-user/edit-user-modal.component';
import { IUser } from '../../core/model/auth.model';
import { Subscription } from 'rxjs';
import { takeWhile } from 'rxjs/operators';
import { ApiService } from '../../core/service/api/api.service';
import { ApiUrl } from '../../core/constant/api.contant';

export type ChartOptions = {
  series: ApexAxisChartSeries;
  chart: ApexChart;
  xaxis: ApexXAxis;
  yaxis: ApexYAxis;
  dataLabels: ApexDataLabels;
  grid: ApexGrid;
  stroke: ApexStroke;
  title: ApexTitleSubtitle;
  annotations: ApexAnnotations
};

@Component({
  selector:    'app-user',
  templateUrl: './user.component.html',
  styleUrls:   [ './user.component.scss' ]
})
export class UserComponent implements OnInit, OnDestroy {

  loginUser: IUser;

  chartOptions: Partial<ChartOptions>;

  SUploadProfileImageRequest: Subscription;
  isUnsubscribe: boolean = false;

  constructor(
    private _layoutHeaderService: LayoutHeaderService,
    private _authService: AuthService,
    private _modalService: LayoutModalService,
    private _apiService: ApiService,
  ) {
  }

  ngOnInit(): void {
    this._layoutHeaderService.show();
    this._layoutHeaderService.themeBlack();
    this._authService.rxLoginUser().pipe(takeWhile(() => this.isUnsubscribe === false)).subscribe(loginUser => {
      this.loginUser = loginUser;
      console.log('loginUser?.image', this.loginUser?.image);
    });
    this.chartOptions = this._getChartOptions();
  }

  ngOnDestroy(): void {
    this.isUnsubscribe = true;
  }

  /******************************     event functions     ****************************/

  onChangeName() {
    this._modalService.create(EditUserModalComponent, {
      save:    () => this._modalService.dismiss(),
      dismiss: () => this._modalService.dismiss()
    });
  }

  onClickCertification(): void {
    alert('개발중입니다.');
  }

  onChangePassword() {
    this._modalService.create(EditPasswordModalComponent, {
      save:    () => this._modalService.dismiss(),
      dismiss: () => this._modalService.dismiss()
    });
  }

  onUploadProfileImage(event: any): void {
    console.log(event);
    const imageFile = event.target.files[0];
    console.log('image', imageFile);
    this.requestUploadProfileImage({ imageFile });
  }

  onLogout() {
    this._authService.logout();
  }

  /******************************     request functions     ****************************/

  private requestUploadProfileImage({ imageFile }: { imageFile: File }) {
    if (this.SUploadProfileImageRequest) { // 연속 처리 방지
      alert('잠시만 기다려주세요.');
      return;
    }

    const formData: FormData = new FormData();
    formData.append('image', imageFile, imageFile.name);

    const url                                  = ApiUrl.profileMe;
    const body                                 = formData;
    this.SUploadProfileImageRequest = this._apiService.patch({
      url,
      body
    })?.pipe(takeWhile(() => this.isUnsubscribe === false))
      .subscribe(res => {
        console.log('requestUploadProfileImage res => ', res);
        this.SUploadProfileImageRequest = null;

        const loginUser = this._authService.getLoginUser();
        loginUser.image = res.image;
        this._authService.updateLoginUser(loginUser);
      }, error => {
        console.log('requestUploadProfileImage error =>', error);
        this.SUploadProfileImageRequest = null;
      });
  }


  /******************************     etc functions     ****************************/


  private _getChartOptions(): Partial<ChartOptions> {
    return {
      series:      [
        {
          name: 'Desktops',
          data: [
            10,
            41,
            35,
            51,
            49,
            62,
            69,
            91,
            148
          ]
        }
      ],
      chart:       {
        width:   300,
        height:  300,
        type:    'line',
        zoom:    {
          enabled: false
        },
        toolbar: {
          show: false
        }
      },
      dataLabels:  {
        enabled: false
      },
      stroke:      {
        curve: 'smooth'
      },
      title:       {
        text: ''
      },
      grid:        {
        show: false
      },
      xaxis:       {
        labels: {
          show: false
        }
      },
      yaxis:       {
        show: false
      },
      annotations: {
        points: [
          {
            x:     5,
            y:     49,
            label: {
              borderColor:  '#c2c2c2',
              borderWidth:  1,
              borderRadius: 2,
              text:         'lv 60',
              textAnchor:   'middle',
              offsetX:      0,
              offsetY:      -15,
              style:        {
                background: '#fff',
                color:      '#777',
                fontSize:   '12px',
                fontWeight: 400,
                fontFamily: undefined,
                cssClass:   'apexcharts-point-annotation-label',
                padding:    {
                  left:   5,
                  right:  5,
                  top:    0,
                  bottom: 2
                }
              }
            }
          }
        ]
      }
    };
  }
}

